import '../styles/MainContent.css';
import React, { useState } from 'react';
import inspiringImage from '../assets/inspiring 1.jpg'; // Adjust the path based on your folder structure

const countriesWithUniversities = {
  // ... same data structure as before ...
};

function MainContent() {
  const [email, setEmail] = useState('');
  const [whatsapp, setWhatsapp] = useState('');
  const [country, setCountry] = useState('');
  const [university, setUniversity] = useState('');
  const [program, setProgram] = useState('');
  const [programs, setPrograms] = useState([]);

  const handleCountryChange = (e) => {
    const selectedCountry = e.target.value;
    setCountry(selectedCountry);
    setUniversity('');
    setProgram('');
    setPrograms([]);
  };

  const handleUniversityChange = (e) => {
    const selectedUniversity = e.target.value;
    setUniversity(selectedUniversity);
    if (selectedCountry && countriesWithUniversities[selectedCountry][selectedUniversity]) {
      setPrograms(countriesWithUniversities[selectedCountry][selectedUniversity]);
    } else {
      setPrograms([]);
    }
    setProgram('');
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = { email, whatsapp, country, university, program };
    // Here you would send formData to the backend
    try {
      const response = await fetch('http://localhost:5001/submit-form', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const responseData = await response.json();
      alert(responseData.message);
    } catch (error) {
      console.error('There was an error!', error);
    }
  };

  return (
    <main className="main-content">
      <div className="inspiring-image-container">
        <img src={inspiringImage} alt="Inspiring" className="inspiring-image" />
      </div>

      <h2>Welcome to Our Education Site</h2>
      <p>We're Building Something Exciting! AfroTouch Education is on its way to help you embark on your educational journey in Estonia and Lithuania.</p>

      <form onSubmit={handleSubmit}>
        <select
          value={country}
          onChange={handleCountryChange}
          required
        >
          <option value="">Select a country</option>
          {Object.keys(countriesWithUniversities).map((country) => (
            <option key={country} value={country}>
              {country}
            </option>
          ))}
        </select>

        {country && (
          <select
            value={university}
            onChange={handleUniversityChange}
            required
          >
            <option value="">Select a university</option>
            {Object.keys(countriesWithUniversities[country] || {}).map((uni) => (
              <option key={uni} value={uni}>
                {uni}
              </option>
            ))}
          </select>
        )}

        {university && (
          <select
            value={program}
            onChange={(e) => setProgram(e.target.value)}
            required
          >
            <option value="">Select a program</option>
            {programs.map((prog) => (
              <option key={prog} value={prog}>
                {prog}
              </option>
            ))}
          </select>
        )}

        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Your email"
          required
        />
        <input
          type="text"
          value={whatsapp}
          onChange={(e) => setWhatsapp(e.target.value)}
          placeholder="Your WhatsApp number"
          required
        />
        <button type="submit">Submit</button>
      </form>

      <p className="privacy-message">
        Your contact information is safe with us. We respect your privacy and will never share your information without your consent.
      </p>
    </main>
  );
}

export default MainContent;
