import React, { useState } from 'react';

function MainContent() {
    const [email, setEmail] = useState('');
    const [whatsapp, setWhatsapp] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // Here we will later add the logic to send data to the backend
        alert(`Email: ${email}, WhatsApp: ${whatsapp}`);
    };

<style jsx>{`
    main {
        text-align: center;
        margin: 20px;
    }
    form {
        margin-top: 20px;
    }
    input {
        margin: 10px;
        padding: 8px;
    }
    button {
        padding: 10px 15px;
        background-color: blue;
        color: white;
        border: none;
        cursor: pointer;
    }
`}</style>


    return (

        <main>
            <h2>Welcome to Our Education Site</h2>
            <p>We're Building Something Exciting! AfroTouch Education is on its way to help you embark on your educational journey in Estonia and Lithuania.</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter your email"
                    required
                />
                <input
                    type="text"
                    value={whatsapp}
                    onChange={(e) => setWhatsapp(e.target.value)}
                    placeholder="Enter your WhatsApp number"
                    required
                />
                <button type="submit">Submit</button>
            </form>
        </main>
    );
}

export default MainContent;
