import '../styles/MainContent.css';

import React, { useState } from 'react';
import inspiringImage from '../assets/inspiring 1.jpg'; // Adjust the path based on your folder structure
import classroomImage from '../assets/classroom.jpg';

const countriesWithUniversities = {
  Estonia: ['Tallinn University', 'University of Tartu'],
  Lithuania: ['Vilnius University', 'Kaunas University of Technology'],
  // ... add more countries and universities as needed
};

function MainContent() {
    const [email, setEmail] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [country, setCountry] = useState('');
    const [university, setUniversity] = useState('');
    const [program, setProgram] = useState('');

    // const handleSubmit = (event) => {
    //     event.preventDefault();
    //     // Here we will later add the logic to send data to the backend
    //     alert(`Email: ${email}, WhatsApp: ${whatsapp}`);
    // };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:5001/submit-form', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, whatsapp }),
            });
            const responseData = await response.json();
            alert(responseData.message);
        } catch (error) {
            console.error('There was an error!', error);
        }
    };


    return (

        <main className="main-content">
            <div className="inspiring-image-container">
                <img src={inspiringImage} alt="InspiringImg" className="inspiring-image" />
            </div>

            <h2>Welcome to Our Education Site</h2>
            <p>We're Building Something Exciting! AfroTouch Education is on its way to help you embark on your educational journey in Estonia and Lithuania.</p>
            <form onSubmit={handleSubmit}>

            <select
            value={country}
            onChange={(e) => {
                setCountry(e.target.value);
                setUniversity(''); // Reset university when country changes
            }}
            required
            >
            <option value="">Select a country</option>
            {Object.keys(countriesWithUniversities).map((country) => (
                <option key={country} value={country}>
                {country}
                </option>
            ))}
            </select>

            {country && (
            <select
                value={university}
                onChange={(e) => setUniversity(e.target.value)}
                required
            >
                <option value="">Select a university</option>
                {countriesWithUniversities[country].map((uni) => (
                <option key={uni} value={uni}>
                    {uni}
                </option>
                ))}
            </select>
            )}

                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Your email"
                    required
                />
                <input
                    type="text"
                    value={whatsapp}
                    onChange={(e) => setWhatsapp(e.target.value)}
                    placeholder="Your WhatsApp number"
                    required
                />
                <button type="submit">Submit</button>
            </form>

            <p className="privacy-message">
                Your contact information is safe with us. We respect your privacy and will never share your information without your consent.
            </p>
        </main>
    );
}

export default MainContent;
