import React, { useState } from 'react';
import '../styles/Header.css';

const countriesWithUniversities = {
    Estonia: {
        "Tallinn University": [
            "BSc in Computer Science",
            "MA in Digital Learning Games",
            "BA in Business Administration",
            "MA in Human-Computer Interaction"
        ],
        "University of Tartu": [
            "MD in Medicine",
            "BSc in Science and Technology",
            "MSc in Software Engineering",
            "MA in Law"
        ],
        "TalTech (Tallinn University of Technology)": [
            "BSc in Cyber Security Engineering",
            "MSc in e-Governance Technologies and Services",
            "BSc in International Business Administration",
            "MSc in Environmental Management and Cleaner Production"
        ],
        "Estonian University of Life Sciences": [
            "MSc in Veterinary Medicine",
            "BSc in Landscape Architecture",
            "MSc in Forestry",
            "MSc in Animal Science"
        ],
      // Add more universities and programs as required
    },
    Lithuania: {
        "Vilnius University": [
            "BSc in Economics",
            "MA in English Philology",
            "MSc in Chemistry",
            "PhD in Physics"
        ],
        "Kaunas University of Technology": [
            "BSc in Mechanical Engineering",
            "MSc in Information Technology",
            "BSc in Civil Engineering",
            "MSc in Industrial Engineering and Management"
        ],
        "Vytautas Magnus University": [
            "BA in Political Science",
            "MA in Sociology",
            "BA in Cultural Management",
            "MA in Marketing and International Commerce"
        ],
        "Klaipėda University": [
            "BSc in Marine Engineering",
            "MA in Education Science",
            "BSc in Public Administration",
            "MSc in Ecology and Environmental Studies"
        ],
      // Add more universities and programs as required
    },Latvia: {
        "University of Daugavpils": [
            "Biology",
            "Chemistry",
            "Eastern European Culture and Business Relations",
            "Environmental Science",
            "History",
            "Physics",
            "Psychology"
        ],
        "Latvia University of Life Sciences and Technologies": [
            "Sustainable Agriculture",
            "Sustainable Forestry",
            "Biosystems Machinery and Technologies",
            "Economics",
            "Computer Control and Computer Science",
            "Information Technologies for Sustainable Development",
            "Sociology of Organizations and Public Administration",
            "Food Quality and Innovations",
            "Geoinformatics and Remote Sensing"
        ],
        "Riga Stradiņš University": [
            "Dentistry",
            "International Business and Start-up Entrepreneurship",
            "Medicine",
            "Audio Speech Therapy",
            "Dental Hygiene",
            "Health Sport",
            "Health Sport Specialist",
            "International Business and Sustainable Economy",
            "International Marketing and Advertising",
            // ... (more programs)
        ],
        // ... (more universities)
    },

    // You can add more countries and their universities and programs similarly
};


function Header(){
    const [email, setEmail] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [country, setCountry] = useState('');
    const [university, setUniversity] = useState('');
    // const [program, setProgram] = useState('');
    const [isFormVisible, setIsFormVisible] = useState(true);
    const [programs, setPrograms] = useState([]);
    const [selectedPrograms, setSelectedPrograms] = useState([]);

    const handleCountryChange = (e) => {
        const selectedCountry = e.target.value;
        setCountry(selectedCountry);
        setUniversity('');
        // setProgram('');
        setPrograms([]);
    };

    const handleUniversityChange = (e) => {
        const selectedUniversity = e.target.value;
        setUniversity(selectedUniversity);
        if (country && countriesWithUniversities[country][selectedUniversity]) {
            setPrograms(countriesWithUniversities[country][selectedUniversity]);
        } else {
            setPrograms([]);
        }
        // setProgram('');
    };

    const handleProgramChange = (e) => {
        const program = e.target.value;
        setSelectedPrograms((prevSelectedPrograms) => 
        e.target.checked
            ? [...prevSelectedPrograms, program]
            : prevSelectedPrograms.filter((prog) => prog !== program)
        );
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = { email, whatsapp, country, university, programs: selectedPrograms };

        // Here you would send formData to the backend
        try {
            const response = await fetch('http://localhost:5001/submit-form', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            // const responseData = await response.json();
            // alert(responseData.message);

            if (response.ok) {
                // Reset form fields
                setEmail('');
                setWhatsapp('');
                setCountry('');
                setUniversity('');
                setSelectedPrograms([]);
                // Display confirmation message
                alert('Your data has been saved. We will get back to you with more details.');
            } else {
                // Handle errors
                alert('There was an issue with your submission. Please try again.');
            }

        } catch (error) {
            console.error('There was an error!', error);
        }
    };

    const toggleFormVisibility = () => {
        setIsFormVisible(!isFormVisible);
    };

    return (
        <header className="header-content">
            <h2 onClick={toggleFormVisibility} className="toggle-form">
                Launch Your Academic Journey!
            </h2>

            {isFormVisible && (
                <div className={`form-container ${isFormVisible ? 'active' : ''}`}>
                    <p> Set Your Sights on Success! Select your country, pick your university, and secure your spot in the program that will define your future.
                        <br /> Start your journey with AfroTouch Education now!
                    </p>

                    <form onSubmit={handleSubmit}>
                        <select
                            value={country}
                            onChange={handleCountryChange}
                            required
                        >
                            <option value="">Select a country</option>
                            {Object.keys(countriesWithUniversities).map((country) => (
                                <option key={country} value={country}>
                                    {country}
                                </option>
                            ))}
                        </select>

                        {country && (
                            <select
                                value={university}
                                onChange={handleUniversityChange}
                                required
                            >
                                <option value="">Select a university</option>
                                {Object.keys(countriesWithUniversities[country] || {}).map((uni) => (
                                    <option key={uni} value={uni}>
                                        {uni}
                                    </option>
                                ))}
                            </select>
                        )}

                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Your email"
                            required
                        />

                        <input
                            type="text"
                            value={whatsapp}
                            onChange={(e) => setWhatsapp(e.target.value)}
                            placeholder="Your WhatsApp number"
                            required
                        />

                        {university && (
                        <div class="checkbox_container">
                            {programs.map((prog) => (
                            <div key={prog}>
                                <input
                                type="checkbox"
                                id={prog}
                                value={prog}
                                checked={selectedPrograms.includes(prog)}
                                onChange={handleProgramChange}
                                />
                                <label htmlFor={prog}>{prog}</label>
                            </div>
                            ))}
                        </div>
                        )}

                        <button type="submit">Sign up</button>
                    </form>

                    <p className="privacy-message">
                        Your contact information is safe with us. We respect your privacy and will never share your information without your consent.
                    </p>
                </div>
            )}
        </header>
    );
}

export default Header;
